INSERT INTO feeds (id, title, description, url, link, image_url, podcast) VALUES ('5ad20e33-a235-4fe1-86b7-83ececa7c06c', 'Pop Culture Happy Hour', 'Get obsessed with us. Four days a week, Pop Culture Happy Hour serves you recommendations and commentary on the buzziest movies, TV, music, books, videogames and more. Join arts journalists Linda Holmes, Glen Weldon, Stephen Thompson, and Aisha Harris - plus a rotating cast of guest pop culture aficionados. The Happy Hour team leaves room at the table for exploring a range of reactions and opinions on every bit of the pop universe. From lowbrow to highbrow to the stuff in between, they take it all with a shot of cheer.<br><br><em>Make your happy hour even happier with Pop Culture Happy Hour Plus! Your subscription supports the podcast and unlocks a sponsor-free feed. </em><em>Learn more at </em><em>plus.npr.org/</em><em>happyhour</em>', 'https://feeds.npr.org/510282/podcast.xml', 'https://www.npr.org/podcasts/510282/pop-culture-happy-hour', 'https://media.npr.org/assets/img/2022/09/23/popculture-happyhour_tile_npr-network-01_sq-8f7dd7b8b06bf1f4d5c063f3752b7b60979624b4.jpg?s=1400&c=66&f=jpg', 'Y');
