INSERT INTO feeds (id, title, description, url, link, image_url, podcast) VALUES ('1c3af55c-142f-4159-9a7b-ff79b9091725', 'It''s Been a Minute', 'Want in on a secret? Your likes and dislikes didn''t develop by accident. There are subtle and not-so-subtle forces around you, shaping what you think, how you act, and even who you think you are. Brittany Luse is here to break the spell and help you feel wiser in a society that makes things blurry.<br><br>THE BEST POP CULTURE PODCAST AWARD WINNER AT THE 2025 SIGNAL AWARDS<br><br>It‚Äôs Been A Minute with Brittany Luse is the best podcast for understanding what‚Äôs going on in culture right now, and helps you consume it smarter. From how politics influences pop culture to how identity influences tech or health, Brittany makes the picture clearer for you every Monday, Wednesday, and Friday.It‚Äôs Been A Minute reaches millions of people every week. Join the community and conversation today.<br><br><em>If you can''t get enough, try It''s Been a Minute Plus. Your subscription supports the show and unlocks a sponsor-free feed. Learn more at plus.npr.org/itsbeenaminute</em>', 'https://feeds.npr.org/510317/podcast.xml', 'https://www.npr.org/podcasts/510317/its-been-a-minute', 'https://media.npr.org/assets/img/2023/02/27/ibam_tile-2023_sq-7803f41ed0370749ef50b8afb21b3d35f64d3870.jpg?s=1400&c=66&f=jpg', 'Y');
INSERT INTO feeds (id, title, description, url, link, image_url, podcast) VALUES ('4d74eb40-7e7a-4f81-a7e3-e690f3e153dd', 'Code Switch', 'What''s CODE SWITCH? It''s the fearless conversations about race that you''ve been waiting for. Hosted by journalists of color, our podcast tackles the subject of race with empathy and humor. We explore how race affects every part of society ‚Äî from politics and pop culture to history, food and everything in between. This podcast makes all of us part of the conversation ‚Äî because we''re all part of the story. Code Switch was named Apple Podcasts'' first-ever Show of the Year in 2020.<br><br><em>Want to level up your Code Switch game? Try Code Switch Plus. </em><em>Your subscription supports the show and unlocks a sponsor-free feed. Learn more at </em><em>plus.npr.org/</em><em>codeswitch</em>', 'https://feeds.npr.org/510312/podcast.xml', 'https://www.npr.org/podcasts/510312/codeswitch', 'https://media.npr.org/assets/img/2022/09/22/code-switch_tile_npr-network-01_sq-d238b5cbd3882f82ea3253c4facbe1f6fc9a78a1.jpg?s=1400&c=66&f=jpg', 'Y');
