INSERT INTO feeds (id, title, description, url, link, image_url, podcast) VALUES ('e189dd48-3b02-44d8-bfc0-ae512ed41109', 'Throughline', '<em>Throughline</em> is a time machine. Each episode, we travel beyond the headlines to answer the question, "How did we get here?" We use sound and stories to bring history to life and put you into the middle of it. From ancient civilizations to forgotten figures, we take you directly to the moments that shaped our world. <em>Throughline</em> is hosted by Peabody Award-winning journalists Rund Abdelfatah and Ramtin Arablouei.<br><br><em>Subscribe to Throughline+. You''ll be supporting the history-reframing, perspective-shifting, time-warping stories you can''t get enough of - and you''ll unlock access bonus episodes and sponsor-free listening. Learn more at plus.npr.org/throughline</em>', 'https://feeds.npr.org/510333/podcast.xml', 'https://www.npr.org/podcasts/510333/throughline', 'https://media.npr.org/assets/img/2024/05/15/throughline_tile-art_sq-a59d7da2ea2bf97562aeec6440ddf837e8f7de65.jpg?s=1400&c=66&f=jpg', 'Y');
