INSERT INTO feeds (id, title, description, url, link, image_url, podcast) VALUES ('a5db859a-2beb-4f2f-8c25-5339b83ab67f', 'Hood Politics with Prop', '<p>The political landscape can be confusing, but it doesn''t have to be. If you''ve survived junior high, lived in an urban city or understand gang life, you can understand geopolitics. Join rapper and author Propaganda and his friends as they use their hood-knowledge to break down the political scene.¬†</p>', 'https://www.omnycontent.com/d/playlist/e73c998e-6e60-432f-8610-ae210140c5b1/7e1493c7-ebf2-4878-bd99-ae3900554de8/71ddab40-adc1-47f6-b7df-ae3900554dff/podcast.rss', 'https://www.iheart.com/podcast/1119-the-hood-politics-with-pr-81527703/', 'https://www.omnycontent.com/d/playlist/e73c998e-6e60-432f-8610-ae210140c5b1/7e1493c7-ebf2-4878-bd99-ae3900554de8/71ddab40-adc1-47f6-b7df-ae3900554dff/image.jpg?t=1703187780&size=Large', 'N');
INSERT INTO feeds (id, title, description, url, link, image_url, podcast) VALUES ('f061a982-ed24-4e57-a029-8545454c3f33', 'Shannon Cason''s Homemade', 'Homemade is short stories and commentary for writer/storyteller, Shannon Cason. Shannon has appeared on countless podcasts and storytelling stages, including Snap Judgment, The Moth, TEDx, and RISK! Shannon Cason''s Homemade lays out his life for the listener, blessings and blemishes.<hr><p style=''color:grey; font-size:0.75em;''> Hosted on Acast. See <a style=''color:grey;'' target=''_blank'' rel=''noopener noreferrer'' href=''https://acast.com/privacy''>acast.com/privacy</a> for more information.</p>', 'https://feeds.acast.com/public/shows/db3b1682-24bf-4d27-80d3-8d23efd5840d', 'http://www.shannoncason.com/', 'https://assets.pippa.io/shows/6100932831fd81f125b34dd1/1701118257559-9721ceef789f632694977e70f9d5bb27.jpeg', 'N');
INSERT INTO feeds (id, title, description, url, link, image_url, podcast) VALUES ('4342a560-289a-4c53-b258-807a044143a5', 'Up First from NPR', 'NPR''s Up First is the news you need to start your day. The three biggest stories of the day, with reporting and analysis from NPR News ‚Äî in 10 minutes. Available weekdays at 6:30 a.m. ET, with hosts Leila Fadel, Steve Inskeep, Michel Martin and A Martinez. Also available on Saturdays at 9 a.m. ET, with Ayesha Rascoe and Scott Simon. On Sundays, hear a longer exploration behind the headlines with Ayesha Rascoe on "The Sunday Story," available by 8 a.m. ET. Subscribe and listen, then support your local NPR station at donate.npr.org.<br><br><em>Support NPR''s reporting by subscribing to Up First+ and unlock sponsor-free listening. Learn more at plus.npr.org/upfirst</em>', 'https://feeds.npr.org/510318/podcast.xml', 'https://www.npr.org/podcasts/510318/up-first', 'https://npr.brightspotcdn.com/dims3/default/strip/false/crop/3000x3000+0+0/resize/1400/quality/66/format/png/?url=http%3A%2F%2Fnpr-brightspot.s3.amazonaws.com%2Fe0%2F7a%2Ffe9d16a44fbf9dd2d9932ffe799b%2F510318.png', 'N');
INSERT INTO feeds (id, title, description, url, link, image_url, podcast) VALUES ('ce4e1791-9065-4f7b-a03f-95bd4aece992', 'Drink Champs', 'Legendary Queens rapper-turned show host N.O.R.E. teams up with Miami hip-hop pioneer DJ EFN for a night of boozy conversation and boisterous storytelling. The hosts and guests engage together in fun, light-hearted conversation - looking back at their paths to success, highlighting their lives, friendships, and iconic moments within their career. In this unfiltered series, drinks are flowing and jokes are rolling - take a seat at the table with N.O.R.E & EFN. You Gotta Relax!', 'https://www.omnycontent.com/d/playlist/e73c998e-6e60-432f-8610-ae210140c5b1/9ff2dac3-12fd-4561-b512-ae33005f64f5/df6d181a-09ea-4bf2-adc6-ae33005f650d/podcast.rss', 'http://www.drinkchamps.com/', 'https://www.omnycontent.com/d/programs/e73c998e-6e60-432f-8610-ae210140c5b1/9ff2dac3-12fd-4561-b512-ae33005f64f5/image.jpg?t=1750703037&size=Large', 'N');
