INSERT INTO feeds (id, title, description, url, link, image_url, podcast) VALUES ('3aed2370-1bbd-4857-8468-0f4a8061a4fa', 'Myths and Legends', '<div>Jason Weiser and Carissa Weiser tell stories from myths, legends, and folklore that have shaped cultures throughout history. Some, like the stories of Aladdin, King Arthur, and Hercules are stories you think you know, but with surprising origins. Others are stories you might not have heard, but really should. All the stories are sourced from world folklore, but retold for modern ears. These are stories of wizards, knights, Vikings, dragons, princesses, and kings from the time when the world beyond the map was a dangerous and wonderful place.¬†</div>', 'https://audioboom.com/channels/5094626.rss', 'https://www.mythpodcast.com/', 'https://audioboom.com/i/40758039.jpg', 'N');
INSERT INTO feeds (id, title, description, url, link, image_url, podcast) VALUES ('7508bacd-866c-4cb2-b240-a3c9a5ce79be', 'NIGHTLIGHT: A Horror Fiction Podcast', '<p>Multi-award winning horror podcast featuring creepy stories with full audio production written by Black writers and performed by Black actors. So scary it‚Äôll make you want to leave your night light on.</p>', 'https://rss.art19.com/nightlight', 'https://art19.com/shows/nightlight', 'https://content.production.cdn.art19.com/images/87/b0/53/5a/87b0535a-4307-49ef-8ee6-b8c409d1972b/7ceb105212954b11c41526f9e33854d87ebc79948e940b22b7618916b7f3ea8d0446e66ccc4b76601a287f66358e565710497b8d1f1ae96cca2856a7aaf3cc17.jpeg', 'N');
